package com.example.tinkoff.data.retrofitimport com.example.tinkoff.data.retrofit.model.MovieDetailTDOimport com.example.tinkoff.data.retrofit.model.TopMovieResponseimport retrofit2.http.GETimport retrofit2.http.Headersimport retrofit2.http.Pathimport retrofit2.http.Queryinterface ApiService {    @Headers("X-API-KEY: $API_KEY")    @GET("v2.2/films/collections")    suspend fun getTopMovie(        @Query(QUERY_PARAM_TYPE) type : String = QUERY_PARAM_POPULAR_MOVIES,        @Query(QUERY_PARAM_PAGE) page : Int    ) : TopMovieResponse    @Headers("X-API-KEY: $API_KEY")    @GET("v2.2/films/{id}")    suspend fun getMovieById(        @Path(QUERY_PARAM_ID) movieId : Int    ) : MovieDetailTDO    private companion object {        // tinkof_api = e30ffed0-76ab-4dd6-b41f-4c9da2b2735b        // my_api = fd9ea270-6cf7-44b5-9623-64c425f68f41        private const val API_KEY = "e30ffed0-76ab-4dd6-b41f-4c9da2b2735b"        private const val QUERY_PARAM_TYPE = "type"        private const val QUERY_PARAM_PAGE = "page"        private const val QUERY_PARAM_ID = "id"        private const val QUERY_PARAM_POPULAR_MOVIES = "TOP_250_MOVIES"    }}